import React from "react";

const ProductGrid = ({ products }) => {
  return (
    <div className="w-full px-6">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product) => (
          <div
            key={product.id}
            className="bg-white rounded-xl shadow-md overflow-hidden transform transition duration-300 hover:-translate-y-2 hover:shadow-xl hover:bg-blue-100 cursor-pointer relative group"
          >
            {/* Image & Overlay */}
            <div className="overflow-hidden rounded-t-xl relative">
              {/* Image */}
              <img
                src={product.image}
                alt={product.name}
                className="w-32 h-48 object-contain mx-auto transition-opacity duration-300 group-hover:opacity-0"
              />
              {/* Overlay Text */}
              <div className="absolute inset-0 flex items-center justify-center bg-blue-600 bg-opacity-90 text-white text-xl font-bold opacity-0 group-hover:opacity-100 transform scale-90 group-hover:scale-100 transition-all duration-300">
                {product.name}
              </div>
            </div>

            {/* Price */}
            <div className="p-4 text-center">
              <p className="text-gray-700 font-bold">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
