import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles, onChange }) => {
  const [titleIndex, setTitleIndex] = useState(0);

  const handleClick = (index) => {
    setTitleIndex(index);
    if (onChange) onChange(index);
  };

  // ...rest of your code
  return (
    <div ref={containerRef} className="relative w-full max-w-md h-64 flex justify-center items-center mx-auto">
      {titles.map((title, index) => {
        const relativeIndex = (index - titleIndex + total) % total;
        const angleStep = (360 / total) * (Math.PI / 180);
        const angle = relativeIndex * angleStep;
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === titleIndex;

        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            className="absolute cursor-pointer px-6 py-4 rounded-[1.5rem] shadow-lg select-none"
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: `translate(-50%, -50%)`,
              zIndex: isActive ? 10 : 1,
              background: isActive ? "linear-gradient(90deg, #ffffff, #ffffff )" : "#f3f4f6",
            }}
            animate={{
              width: isActive ? "290px" : "210px",
              opacity: isActive ? 1 : 0.5,
            }}
            transition={{ duration: 0.3 }}
          >
            <span
              className={`block text-lg ${
                isActive ? "text-blue-600 font-bold" : "text-gray-500"
              }`}
            >
              {title}
            </span>
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
