import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [activeIndex, setActiveIndex] = useState(value);
  const containerRef = useRef(null);
  const [radius, setRadius] = useState(50);

  const handleClick = (index) => {
    setActiveIndex(index);
    onChange?.(index);
  };

  // Adjust radius dynamically based on container width
  useEffect(() => {
    const updateRadius = () => {
      if (containerRef.current) {
        const width = containerRef.current.offsetWidth;
        const height = containerRef.current.offsetHeight;
        // Bigger radius on desktop
        setRadius(window.innerWidth >= 768 ? Math.min(width / 2, 80) : 50);
      }
    };
    updateRadius();
    window.addEventListener("resize", updateRadius);
    return () => window.removeEventListener("resize", updateRadius);
  }, []);

  const total = titles.length;

  return (
    <div
      ref={containerRef}
      className="relative w-full max-w-[220px] h-[200px] flex justify-center items-center"
    >
      {titles.map((title, index) => {
        const angle = (index / total) * 2 * Math.PI - Math.PI / 2;
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === activeIndex;

        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            whileHover={{ scale: 1.15, boxShadow: "0 5px 15px rgba(0,0,0,0.2)" }}
            className={`absolute cursor-pointer px-4 py-2 rounded-xl shadow-md text-center text-sm transition-colors duration-300 ${
              isActive
                ? "bg-white text-blue-600 font-bold"
                : "bg-gray-200 text-gray-700 hover:bg-white hover:text-blue-600"
            }`}
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: "translate(-50%, -50%)",
              zIndex: isActive ? 20 : 5,
              whiteSpace: "nowrap",
            }}
            animate={{ scale: isActive ? 1.3 : 1 }}
            transition={{ duration: 0.3 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
