import React from "react";

const ProductGrid = ({ products }) => {
  // Array of modern gradient or solid colors
  const colors = [
    "bg-gradient-to-br from-pink-500 to-pink-600",
    "bg-gradient-to-br from-blue-500 to-indigo-600",
    "bg-gradient-to-br from-green-500 to-emerald-600",
    "bg-gradient-to-br from-purple-500 to-violet-600",
    "bg-gradient-to-br from-orange-500 to-red-600",
  ];

  return (
    <div className="w-full px-6">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product, index) => (
          <div
            key={product.id}
            className={`group relative rounded-xl shadow-md overflow-hidden transform transition-all duration-300 hover:-translate-y-2 hover:shadow-xl cursor-pointer`}
          >
            {/* Conditional Rendering */}
            {index === 0 ? (
              // ✅ First grid item with custom text
              <div
                className={`flex items-center justify-center h-64 p-6 text-center text-white font-extrabold text-3xl ${colors[index % colors.length]}`}
              >
                Feel free to browse our wares
              </div>
            ) : (
              // ✅ Other grid items
              <div className="relative w-full h-64 overflow-hidden">
                <div
                  className={`absolute inset-0 ${colors[index % colors.length]} opacity-10`}
                ></div>
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-32 h-48 object-contain mx-auto transition duration-300 group-hover:opacity-75 relative z-10"
                />
                {/* Overlay */}
                <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition duration-300 z-20">
                  <span className="text-white text-lg font-semibold">
                    {product.name}
                  </span>
                </div>
              </div>
            )}

            {/* Product Info */}
            {index !== 0 && (
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
                <p className="text-gray-700 font-bold">{product.price}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
