import React from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {

  return (
    <div className="flex flex-col items-center md:items-end gap-4">
      {titles.map((title, index) => {
        const isActive = index === value;
        return (
          <motion.div
            key={index}
            onClick={() => onChange(index)}
            whileHover={{ scale: 1.05, boxShadow: "0 5px 15px rgba(0,0,0,0.15)" }}
            animate={{ scale: isActive ? 1.1 : 1 }}
            transition={{ duration: 0.2 }}
            className={`cursor-pointer px-4 py-2 rounded-lg text-center text-sm transition-colors duration-300 ${
              isActive
                ? "bg-white text-blue-600 font-bold shadow-md"
                : "bg-gray-200 text-gray-700 hover:bg-white hover:text-blue-600"
            }`}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
