import { motion, useScroll, useTransform } from 'framer-motion';
import { useRef } from 'react';

const ParallaxPhrases = ({ phrases }) => {
  const sectionRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start start", "end start"],
  });

  // Single motion for the whole section
  const yMotion = useTransform(scrollYProgress, [0, 1], [50, -50]);

  return (
    <section
      ref={sectionRef}
      className="relative h-screen flex flex-col items-center justify-center overflow-hidden bg-yellow-50"
    >
      {phrases.map((phrase, i) => (
        <motion.h1
          key={i}
          style={{ y: yMotion }}
          className={`absolute text-4xl md:text-6xl font-bold text-yellow-700 text-center px-4`}
        >
          {phrase}
        </motion.h1>
      ))}
    </section>
  );
};

export default ParallaxPhrases;
