// src/pages/Home.jsx
import React, { useState } from "react";
import { motion } from "framer-motion";
import RotatingTitle from "../components/RotatingTitle";

const TITLES = ["Overview", "What do we have?", "Rare stuff"];
const CONTENT = [
  "This is the overview of our platform. Learn what we do and how we help.",
  "We have electronics, books, toys, clothes, and more — curated for you.",
  "Hunting for unique pieces? Explore our rare and exclusive items."
];

export default function Home() {
  const [selected, setSelected] = useState(0);

  return (
    <div className="font-sans">
      <section
        id="titles"
        className="bg-gradient-to-br from-blue-700 to-blue-900 py-12"
      >
        <div className="max-w-6xl mx-auto px-6 grid lg:grid-cols-2 gap-10 items-center">
          {/* Left: Rotating titles */}
          <div className="flex justify-center">
            <RotatingTitle titles={TITLES} value={selected} onChange={setSelected} />
          </div>

          {/* Right: Content */}
          <motion.div
            key={selected}
            initial={{ opacity: 0, x: 24 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.35 }}
            className="text-white"
          >
            <h3 className="text-3xl font-bold mb-3">{TITLES[selected]}</h3>
            <p className="text-lg/7 opacity-90">{CONTENT[selected]}</p>
          </motion.div>
        </div>
      </section>
    </div>
  );
}
