import React, { useState, useEffect } from "react";
import { FiMenu } from "react-icons/fi";
import "../assets/css/home.css"; // custom CSS

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = ["Home", "About", "Products", "Features", "Contact"];

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`navbar ${isScrolled ? "scrolled" : ""}`}>
      <nav className="nav-container">
        <h1 className={`brand ${isScrolled ? "hide" : ""}`}>BrandName</h1>

        <div className="nav-right">
          {/* Full nav links (fade out when scrolled) */}
          <ul className={`nav-links ${isScrolled ? "fade-out" : "fade-in"}`}>
            {navItems.map((item) => (
              <li key={item}>
                <a href={`#${item.toLowerCase()}`}>{item}</a>
              </li>
            ))}
          </ul>

          {/* Circle menu button (fade in when scrolled) */}
          <div
            className={`circle-menu-container ${
              isScrolled ? "fade-in" : "fade-out"
            }`}
          >
            <button
              className="circle-menu-button"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              <FiMenu className="menu-icon" />
            </button>

            <div className={`dropdown ${isMenuOpen ? "open" : ""}`}>
              <ul>
                {navItems.map((item) => (
                  <li key={item}>
                    <a
                      href={`#${item.toLowerCase()}`}
                      onClick={() => setIsMenuOpen(false)}
                    >
                      {item}
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
