// src/components/ProductGrid.jsx
import React from "react";

const ProductGrid = ({ products }) => {
  return (
    <div className="w-full max-w-[1400px] mx-auto px-4">
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {products.map((product) => (
          <div
            key={product.id}
            className="flex flex-col bg-white rounded-xl shadow-md overflow-hidden transition-transform duration-300 hover:-translate-y-1 hover:shadow-xl"
          >
            {/* Image */}
            <div className="h-44 overflow-hidden">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
              />
            </div>

            {/* Product Info */}
            <div className="p-4 text-center flex flex-col justify-between flex-1">
              <h2 className="text-lg font-semibold mb-1 truncate">{product.name}</h2>
              <p className="text-gray-700 font-bold">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
