import React, { useState } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [titleIndex, setTitleIndex] = useState(value || 0);
  const total = titles.length;
  const activeIndex = titleIndex;
  const radius = 60; // reduce radius so bubbles fit within left column

  const handleClick = (index) => {
    setTitleIndex(index);
    onChange?.(index);
  };

  return (
    <div className="relative w-64 h-64 flex justify-center items-center">
      {titles.map((title, index) => {
        const relativeIndex = (index - activeIndex + total) % total;
        const angleStep = (2 * Math.PI) / total;
        const angle = relativeIndex * angleStep;

        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === activeIndex;

        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            className={`absolute cursor-pointer px-4 py-2 rounded-lg shadow select-none text-center ${
              isActive ? "bg-white text-blue-600 font-bold" : "bg-gray-200 text-gray-600"
            }`}
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              zIndex: isActive ? 20 : 5,
              transform: "translate(-50%, -50%)",
            }}
            animate={{ scale: isActive ? 1.2 : 1 }}
            transition={{ duration: 0.3 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
