import { motion } from "framer-motion";
import { Mail, Phone, Edit3 } from "lucide-react";

const buttonVariants = {
  hidden: { y: 40, opacity: 0 },
  visible: (i) => ({
    y: 0,
    opacity: 1,
    transition: { delay: i * 0.15, duration: 0.6, ease: "easeOut" },
  }),
};

export default function ParallaxSection() {
  const buttons = [
    { label: "Email Us", href: "mailto:someone@example.com", icon: <Mail size={18} /> },
    { label: "Call Us", href: "tel:+1234567890", icon: <Phone size={18} /> },
    { label: "Contact Form", href: "#form", icon: <Edit3 size={18} /> },
  ];

  return (
    <section
      id="contact"
      className="h-screen flex flex-col items-center justify-center bg-gradient-to-br from-purple-100 via-pink-100 to-indigo-200 px-6 relative overflow-hidden"
    >
      {/* Decorative gradient orbs */}
      <div className="absolute top-20 left-20 w-40 h-40 bg-purple-400/30 rounded-full blur-3xl animate-pulse" />
      <div className="absolute bottom-20 right-20 w-56 h-56 bg-pink-400/30 rounded-full blur-3xl animate-pulse" />

      {/* Title */}
      <motion.h1
        initial={{ y: 100, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true, amount: 0.5 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-5xl md:text-6xl font-extrabold text-center bg-gradient-to-r from-purple-600 via-pink-500 to-indigo-600 bg-clip-text text-transparent"
      >
        Let’s Get in Touch
      </motion.h1>

      {/* Subheading */}
      <motion.p
        initial={{ y: 40, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true, amount: 0.5 }}
        transition={{ duration: 0.7, ease: "easeOut", delay: 0.2 }}
        className="mt-4 text-lg md:text-xl text-gray-700 text-center max-w-xl"
      >
        We’d love to hear from you — reach out through any of the options below.
      </motion.p>

      {/* Buttons in a glass card */}
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        whileInView={{ opacity: 1, scale: 1 }}
        viewport={{ once: true, amount: 0.4 }}
        transition={{ duration: 0.6, ease: "easeOut", delay: 0.3 }}
        className="mt-12 flex flex-wrap gap-6 justify-center p-6 rounded-3xl bg-white/30 backdrop-blur-xl border border-white/40 shadow-xl"
      >
        {buttons.map((btn, i) => (
          <motion.a
            key={btn.label}
            href={btn.href}
            custom={i}
            variants={buttonVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.5 }}
            whileHover={{
              scale: 1.08,
              y: -5,
              boxShadow: "0px 8px 25px rgba(139,92,246,0.3)",
              background:
                "linear-gradient(135deg, rgba(255,255,255,0.7) 0%, rgba(255,255,255,0.9) 100%)",
            }}
            whileTap={{ scale: 0.95 }}
            className="flex items-center gap-2 px-8 py-3 rounded-2xl bg-white/40 border border-white/60 shadow-md text-purple-800 font-medium transition-all"
          >
            {btn.icon}
            {btn.label}
          </motion.a>
        ))}
      </motion.div>
    </section>
  );
}
