import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles }) => {
  const [titleIndex, setTitleIndex] = useState(0);
  const containerRef = useRef(null);
  const [radius, setRadius] = useState(120);

  // Update radius on resize for responsiveness
  useEffect(() => {
    const handleResize = () => {
      if (containerRef.current) {
        const size = containerRef.current.offsetWidth;
        setRadius(size / 2 - 50); // padding from edges
      }
    };
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const total = titles.length;

  return (
    <div
      ref={containerRef}
      className="relative w-full max-w-md h-64 flex justify-center items-center mx-auto"
    >
      {titles.map((title, index) => {
        const relativeIndex = (index - titleIndex + total) % total;
        const angle = (relativeIndex * 360) / total;
        const rad = (angle * Math.PI) / 180;
        const x = radius * Math.cos(rad);
        const y = radius * Math.sin(rad);
        const isActive = index === titleIndex;

        return (
          <motion.div
            key={index}
            onClick={() => setTitleIndex(index)}
            className="absolute cursor-pointer px-4 py-2 rounded-lg shadow-lg transition-all duration-500"
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: `translate(-50%, -50%) scale(${isActive ? 1.2 : 0.9})`,
              zIndex: isActive ? 10 : 1,
              opacity: isActive ? 1 : 0.5,
              backgroundColor: isActive ? "#BFDBFE" : "#E5E7EB",
            }}
            animate={{ opacity: isActive ? 1 : 0.5 }}
          >
            <span
              className={`block text-center text-sm md:text-lg font-semibold ${
                isActive ? "text-blue-600" : "text-gray-500"
              }`}
            >
              {title}
            </span>
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
