// src/components/RotatingTitle.jsx
import React from "react";
import { motion } from "framer-motion";

export default function RotatingTitle({ titles = [], value = 0, onChange }) {
  const total = Math.max(titles.length, 1);
  const activeIndex = ((value % total) + total) % total; // safe mod
  const radius = 120; // fixed radius to avoid refs/effects

  return (
    <div className="relative w-full max-w-md h-64 flex justify-center items-center mx-auto">
      {titles.map((title, index) => {
        const relativeIndex = (index - activeIndex + total) % total;
        const angleStep = (2 * Math.PI) / total;
        const angle = relativeIndex * angleStep;

        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === activeIndex;

        return (
          <motion.div
            key={index}
            onClick={() => onChange?.(index)}
            className="absolute cursor-pointer px-6 py-4 rounded-[1.5rem] shadow-lg select-none"
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: `translate(-50%, -50%)`,
              zIndex: isActive ? 10 : 1,
              background: isActive ? "#ffffff" : "#f3f4f6",
            }}
            animate={{
              width: isActive ? "290px" : "210px",
              opacity: isActive ? 1 : 0.5,
            }}
            transition={{ duration: 0.3 }}
          >
            <span className={`block text-lg ${isActive ? "text-blue-600 font-bold" : "text-gray-500"}`}>
              {title}
            </span>
          </motion.div>
        );
      })}
    </div>
  );
}
