import React, { useState } from "react";
import Logo from "../../assets/images/gundamhead.png";

const Signup = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Signup data:", formData);
  };

  return (
    <div className="min-h-screen flex flex-row gap-8 px-12 bg-gray-50">
      {/* Left: Signup Form */}
      <div className="min-h-screen flex flex-row bg-gray-50 px-12 py-8 gap-8">
  {/* Left: Signup Form */}
  <div className="flex-[0.4] flex items-center justify-center">
    <div className="bg-white w-full max-w-md p-10 rounded-2xl shadow-2xl">
      <h2 className="text-3xl font-extrabold text-gray-900 text-center mb-4">
        Create Your Account
      </h2>
      <form className="space-y-5">
        <input
          type="text"
          placeholder="Username"
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />
        <input
          type="email"
          placeholder="Email"
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />
        <input
          type="password"
          placeholder="Confirm Password"
          className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />
        <button className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition">
          Sign Up
        </button>
      </form>
      <p className="text-center text-sm text-gray-600 mt-4">
        Already have an account?{" "}
        <a href="/login" className="text-blue-600 hover:underline">
          Log in
        </a>
      </p>
    </div>
  </div>

  {/* Right: Logo */}
  <div className="flex-[0.6] bg-blue-600 flex items-center justify-center rounded-2xl shadow-xl p-8">
    <img src={Logo} alt="Logo" className="w-72 h-auto object-contain drop-shadow-xl" />
  </div>
</div>

      {/* Right: Logo */}
      <div className="flex-1 bg-blue-600 flex items-center justify-center p-8">
        <img
          src={Logo}
          alt="Logo"
          className="w-72 h-auto object-contain drop-shadow-xl"
        />
      </div>
    </div>
  );
};

export default Signup;
