import { motion } from "framer-motion";
import { Mail, Phone, Edit3 } from "lucide-react";

const buttonVariants = {
  hidden: { y: 40, opacity: 0 },
  visible: (i) => ({
    y: 0,
    opacity: 1,
    transition: { delay: i * 0.15, duration: 0.6, ease: "easeOut" },
  }),
};

export default function ParallaxSection() {
  const buttons = [
    { label: "Email Us", href: "mailto:someone@example.com", icon: <Mail size={18} /> },
    { label: "Call Us", href: "tel:+1234567890", icon: <Phone size={18} /> },
    { label: "Contact Form", href: "#form", icon: <Edit3 size={18} /> },
  ];

  return (
    <section
      id="contact"
      className="h-screen flex flex-col items-center justify-center 
                 bg-gradient-to-br from-gray-900 via-gray-800 to-black px-6 relative overflow-hidden"
    >
      {/* Subtle background grid */}
      <div className="absolute inset-0 bg-[linear-gradient(to_right,rgba(255,255,255,0.05)_1px,transparent_1px),linear-gradient(to_bottom,rgba(255,255,255,0.05)_1px,transparent_1px)] bg-[size:40px_40px]" />

      {/* Title */}
      <motion.h1
        initial={{ y: 100, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true, amount: 0.5 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-5xl md:text-6xl font-extrabold text-center text-white relative z-10"
      >
        Let’s Get in Touch
      </motion.h1>

      {/* Subheading */}
      <motion.p
        initial={{ y: 40, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true, amount: 0.5 }}
        transition={{ duration: 0.7, ease: "easeOut", delay: 0.2 }}
        className="mt-4 text-lg md:text-xl text-gray-300 text-center max-w-xl relative z-10"
      >
        We’d love to hear from you — reach out through any of the options below.
      </motion.p>

      {/* Buttons */}
      <div className="mt-12 flex flex-wrap gap-6 justify-center relative z-10">
        {buttons.map((btn, i) => (
          <motion.a
            key={btn.label}
            href={btn.href}
            custom={i}
            variants={buttonVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.5 }}
            whileHover={{
              scale: 1.05,
              y: -4,
              boxShadow: "0px 6px 20px rgba(139,92,246,0.4)",
            }}
            whileTap={{ scale: 0.96 }}
            className="flex items-center gap-3 px-6 py-3 rounded-xl border border-gray-600 
                       text-gray-200 font-medium hover:border-purple-500 hover:text-white transition-all"
          >
            <span className="p-2 rounded-full bg-gray-800 text-purple-400">{btn.icon}</span>
            {btn.label}
          </motion.a>
        ))}
      </div>
    </section>
  );
}
