import { motion } from "framer-motion";

const buttonVariants = {
  hidden: { y: 40, opacity: 0 },
  visible: (i) => ({
    y: 0,
    opacity: 1,
    transition: { delay: i * 0.15, duration: 0.6, ease: "easeOut" },
  }),
};

export default function ParallaxSection() {
  const buttons = [
    { label: "Email Us", href: "mailto:someone@example.com" },
    { label: "Call Us", href: "tel:+1234567890" },
    { label: "Contact Form", href: "#form" },
  ];

  return (
    <section
      id="contact"
      className="h-screen flex flex-col items-center justify-center bg-gradient-to-br from-purple-100 to-purple-200 px-6"
    >
      {/* Title */}
      <motion.h1
        initial={{ y: 100, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        viewport={{ once: true, amount: 0.5 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-5xl md:text-6xl font-extrabold text-purple-700 text-center"
      >
        Let's Get in Touch
      </motion.h1>

      {/* Sleek Buttons */}
      <div className="mt-12 flex flex-wrap gap-6 justify-center">
        {buttons.map((btn, i) => (
          <motion.a
            key={btn.label}
            href={btn.href}
            custom={i}
            variants={buttonVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.5 }}
            whileHover={{ scale: 1.05, y: -4 }}
            whileTap={{ scale: 0.97 }}
            className="px-8 py-3 rounded-2xl backdrop-blur-md bg-white/40 border border-white/50 shadow-md text-purple-800 font-medium hover:bg-white/60 transition"
          >
            {btn.label}
          </motion.a>
        ))}
      </div>
    </section>
  );
}
