import { motion, useScroll, useTransform } from 'framer-motion';
import { useRef } from 'react';

const ParallaxPhrases = ({ phrases }) => {
  const sectionRef = useRef(null);

  // Track scroll relative to section
  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start start", "end start"],
  });

  // Single transform hook
  const yMotion = useTransform(scrollYProgress, [0, 1], [50, -50]);

  return (
    <section
      ref={sectionRef}
      className="relative h-screen flex items-center justify-center overflow-hidden bg-yellow-50"
    >
      {phrases.map((phrase, index) => (
        <motion.h1
          key={index}
          style={{
            y: yMotion,
            // Apply a small offset per phrase
            translateY: `${index * 20}px`,
          }}
          className="absolute text-4xl md:text-6xl font-bold text-yellow-700 text-center px-4"
        >
          {phrase}
        </motion.h1>
      ))}
    </section>
  );
};

export default ParallaxPhrases;
