import React, { useState } from "react";
import { motion } from "framer-motion";

const ProductGrid = ({ products }) => {
  const hoverColors = ["#D3D3D3", "#B6D0E2", "#9FE2BF", "#FEF3C7", "#e5d0ff", "#FAA0A0"];
  const overlayColors = ["#818589", "#4682B4", "#10B981", "#FFD700", "#bf8bff", "#EC5800"];

  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <div className="w-full px-6">
      <div className="flex flex-nowrap md:flex-wrap gap-4 md:gap-8 overflow-x-auto md:overflow-visible">
        {products.map((product, index) => {
          const hoverColor = hoverColors[index % hoverColors.length];
          const overlayColor = overlayColors[index % overlayColors.length];

          return (
            <div
              key={product.id}
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
              style={{ backgroundColor: hoveredIndex === index ? hoverColor : "#F3F4F6" }}
              className="group relative rounded-xl shadow-md flex-shrink-0 w-[200px] md:w-auto md:flex-1 transform transition-all duration-300 hover:-translate-y-1 hover:shadow-xl cursor-pointer"
            >
              <div className="relative w-full h-64 overflow-hidden flex items-center justify-center">
                <motion.img
                  src={product.image}
                  alt={product.name}
                  className="w-32 h-48 object-contain mx-auto z-0"
                  initial={{ scale: 1 }}
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.6 }}
                />
                <div
                  style={{ backgroundColor: overlayColor }}
                  className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition duration-500 z-10 pointer-events-none"
                >
                  <span className="text-white text-lg font-semibold">{product.name}</span>
                </div>
              </div>
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold">{product.name}</h3>
                <p className="text-gray-700 font-bold">{product.price}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductGrid;
