import { motion } from "framer-motion";
import { Mail, Phone, Edit3 } from "lucide-react";

const buttonVariants = {
  hidden: { y: 40, opacity: 0 },
  visible: (i) => ({
    y: 0,
    opacity: 1,
    transition: { delay: i * 0.15, duration: 0.6, ease: "easeOut" },
  }),
};

export default function ParallaxSection() {
  const buttons = [
    { label: "Email Us", href: "mailto:someone@example.com", icon: <Mail size={18} /> },
    { label: "Call Us", href: "tel:+1234567890", icon: <Phone size={18} /> },
    { label: "Contact Form", href: "#form", icon: <Edit3 size={18} /> },
  ];

  return (
    <section
      id="contact"
      className="h-screen relative flex items-center justify-center bg-gradient-to-br from-purple-100 via-pink-100 to-indigo-200 px-8"
    >
      {/* Decorative floating orbs */}
      <div className="absolute top-16 left-16 w-40 h-40 bg-purple-400/30 rounded-full blur-3xl animate-pulse"></div>
      <div className="absolute bottom-16 right-16 w-56 h-56 bg-pink-400/30 rounded-full blur-3xl animate-pulse"></div>

      {/* Main grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-12 max-w-6xl w-full items-center">
        {/* LEFT CARD */}
        <motion.div
          initial={{ x: -50, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          viewport={{ once: true, amount: 0.5 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="flex flex-col justify-center text-center md:text-left p-8 bg-white/30 backdrop-blur-xl border border-white/40 rounded-3xl shadow-xl"
        >
          <h1 className="text-5xl md:text-6xl font-extrabold bg-gradient-to-r from-purple-600 via-pink-500 to-indigo-600 bg-clip-text text-transparent">
            Let’s Get in Touch
          </h1>
          <p className="mt-4 text-lg md:text-xl text-gray-700 max-w-md mx-auto md:mx-0">
            We’d love to hear from you — reach out through any of the options below.
          </p>
        </motion.div>

        {/* RIGHT BUTTONS CARD */}
        <motion.div
          initial={{ x: 50, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          viewport={{ once: true, amount: 0.5 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="flex flex-col items-center md:items-start gap-6 p-8 bg-white/30 backdrop-blur-xl border border-white/40 rounded-3xl shadow-xl"
        >
          {buttons.map((btn, i) => (
            <motion.a
              key={btn.label}
              href={btn.href}
              custom={i}
              variants={buttonVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true, amount: 0.5 }}
              whileHover={{
                scale: 1.08,
                y: -4,
                boxShadow: "0px 12px 30px rgba(139,92,246,0.3)",
                background:
                  "linear-gradient(135deg, rgba(255,255,255,0.7) 0%, rgba(255,255,255,0.9) 100%)",
              }}
              whileTap={{ scale: 0.96 }}
              className="w-full md:w-auto flex items-center gap-3 px-8 py-4 rounded-2xl text-purple-800 font-medium transition-all"
            >
              {btn.icon}
              {btn.label}
            </motion.a>
          ))}
        </motion.div>
      </div>
    </section>
  );
}
