import React from "react";

const ProductGrid = ({ products }) => {
  return (
    <div className="max-w-6xl mx-auto px-6">
  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
    {products.map((product) => (
      <div key={product.id} className="bg-white rounded-xl shadow-md overflow-hidden transform transition hover:-translate-y-1 hover:shadow-lg cursor-pointer">
        <div className="overflow-hidden rounded-t-xl flex items-center justify-center bg-gray-100">
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-40 object-contain transition-transform duration-300 hover:scale-105"
          />
        </div>
        <div className="p-3 text-center">
          <h2 className="text-md font-semibold mb-1">{product.name}</h2>
          <p className="text-gray-700 font-medium">{product.price}</p>
        </div>
      </div>
    ))}
  </div>
</div>
  );
};

export default ProductGrid;
