import React from "react";

const ProductGrid = ({ products }) => {
  return (
    <div className="max-w-[1400px] mx-auto px-6">
      {/* Grid with proper gaps */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product) => (
          <div
            key={product.id}
            className="bg-white rounded-lg shadow-md overflow-hidden transition hover:shadow-xl w-full max-w-[280px] mx-auto"
          >
            {/* Image container */}
            <div className="overflow-hidden rounded-t-lg h-40 flex items-center justify-center bg-gray-100">
              <img
                src={product.image}
                alt={product.name}
                className="max-h-full max-w-full object-contain"
              />
            </div>
            {/* Product Info */}
            <div className="p-4 text-center">
              <h2 className="text-md font-semibold mb-1">{product.name}</h2>
              <p className="text-gray-600 text-sm font-bold">{product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
