import { motion, useScroll, useTransform } from 'framer-motion';
import { useRef, useState } from 'react';

const ParallaxPhrases = ({ phrases }) => {
  const sectionRef = useRef(null);
  const [phraseIndex, setPhraseIndex] = useState(0);

  // Track scroll relative to this section
  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start end", "end start"], // start = top enters viewport, end = bottom leaves
  });

  // Smaller motion range for visible parallax
  const yMotion = useTransform(scrollYProgress, [0, 1], [50, -50]);

  const handleNext = () => setPhraseIndex((prev) => (prev + 1) % phrases.length);
  const handlePrev = () => setPhraseIndex((prev) => (prev - 1 + phrases.length) % phrases.length);

  return (
    <section
      ref={sectionRef}
      className="relative h-screen flex flex-col items-center justify-center overflow-hidden bg-yellow-50"
    >
      <motion.div style={{ y: yMotion }} className="flex flex-col items-center justify-center">
        <div className="h-24 flex items-center justify-center">
          <motion.div
            key={phrases[phraseIndex]}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
            className="text-3xl font-semibold text-yellow-700"
          >
            {phrases[phraseIndex]}
          </motion.div>
        </div>

        <div className="mt-6 flex gap-4">
          <button
            onClick={handlePrev}
            className="px-5 py-2 bg-yellow-200 rounded hover:bg-yellow-300 transition-colors"
          >
            Prev
          </button>
          <button
            onClick={handleNext}
            className="px-5 py-2 bg-yellow-200 rounded hover:bg-yellow-300 transition-colors"
          >
            Next
          </button>
        </div>
      </motion.div>
    </section>
  );
};

export default ParallaxPhrases;
