import React, { useState } from "react";
import { motion } from "framer-motion";

const ProductGrid = ({ products }) => {
  const hoverColors = ["#D3D3D3","#B6D0E2", "#9FE2BF", "#FEF3C7", "#e5d0ff", "#FAA0A0"];
  const overlayColors = ["#818589","#4682B4", "#10B981", "#FFD700", "#bf8bff", "#EC5800"];

  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const next = () => {
    setCurrentIndex((prev) => (prev + 1) % products.length);
  };

  const prev = () => {
    setCurrentIndex((prev) => (prev - 1 + products.length) % products.length);
  };

  return (
    <div className="w-full px-6">
      {/* Desktop Grid */}
      <div className="hidden sm:grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product, index) => {
          const hoverColor = hoverColors[index % hoverColors.length];
          const overlayColor = overlayColors[index % overlayColors.length];

          return (
            <div
              key={product.id}
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
              style={{
                backgroundColor: hoveredIndex === index ? hoverColor : "#F3F4F6",
              }}
              className="group relative rounded-xl shadow-md overflow-hidden transform transition-all duration-300 hover:-translate-y-1 hover:shadow-xl cursor-pointer"
            >
              {index === 0 ? (
                <div className="flex items-center justify-center h-64 p-6 text-center">
                  <h2 className="text-3xl font-extrabold text-gray-700">
                    Feel free to browse our wares
                  </h2>
                </div>
              ) : (
                <div className="relative w-full h-64 overflow-hidden flex items-center justify-center">
                  <motion.img
                    src={product.image}
                    alt={product.name}
                    className="w-32 h-48 object-contain mx-auto z-0"
                    initial={{ scale: 1, y: 0 }}
                    whileHover={{ scale: 1.05, y: [-10, -20, -10, 0] }}
                    transition={{ duration: 0.6, ease: "easeOut" }}
                  />
                  <div
                    style={{ backgroundColor: overlayColor }}
                    className="absolute inset-0 flex items-center justify-center opacity-0 group-hover:opacity-100 transition duration-500 z-10 pointer-events-none"
                  >
                    <span className="text-white text-lg font-semibold">{product.name}</span>
                  </div>
                </div>
              )}
              {index !== 0 && (
                <div className="p-4 text-center">
                  <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
                  <p className="text-gray-700 font-bold">{product.price}</p>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* Mobile Carousel */}
      <div className="sm:hidden relative flex items-center justify-center">
        <button
          onClick={prev}
          className="absolute left-0 bg-gray-200 px-3 py-2 rounded-full shadow"
        >
          ◀
        </button>

        <div className="w-64">
          {products.length > 0 && (
            <div
              key={products[currentIndex].id}
              className="bg-gray-100 rounded-xl shadow-md overflow-hidden"
            >
              <div className="flex flex-col items-center justify-center h-64">
                {products[currentIndex].image ? (
                  <img
                    src={products[currentIndex].image}
                    alt={products[currentIndex].name}
                    className="w-32 h-48 object-contain"
                  />
                ) : (
                  <h2 className="text-2xl font-bold text-gray-700">Browse our wares</h2>
                )}
              </div>
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold">{products[currentIndex].name}</h3>
                {products[currentIndex].price && (
                  <p className="text-gray-700 font-bold">{products[currentIndex].price}</p>
                )}
              </div>
            </div>
          )}
        </div>

        <button
          onClick={next}
          className="absolute right-0 bg-gray-200 px-3 py-2 rounded-full shadow"
        >
          ▶
        </button>
      </div>
    </div>
  );
};

export default ProductGrid;
