import React, { useState, useEffect } from "react";
import { FiMenu } from "react-icons/fi";
import '../assets/css/home.css';

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const navItems = ["Home", "About", "Products", "Features", "Contact"];

  return (
    <header className={`fixed w-full z-50 transition-colors duration-300 ${isScrolled ? "bg-white/90 shadow-md" : "bg-transparent"}`}>
      <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo */}
        <h1 className="text-2xl font-extrabold text-blue-600 tracking-wide">
          BrandName
        </h1>

        {/* Desktop Nav Links */}
        <ul className={`hidden md:flex gap-10 text-md font-medium transition-opacity duration-500 ${isScrolled ? "opacity-70" : "opacity-100"}`}>
          {navItems.map(item => (
            <li key={item}>
              <a
                href={`#${item.toLowerCase()}`}
                className="relative hover:text-blue-500 after:content-[''] after:absolute after:w-0 after:h-[2px] after:bg-blue-500 after:left-0 after:-bottom-1 after:transition-all after:duration-300 hover:after:w-full"
              >
                {item}
              </a>
            </li>
          ))}
        </ul>

        {/* Mobile Hamburger */}
        <div className="md:hidden relative">
          <button onClick={() => setIsMenuOpen(!isMenuOpen)}>
            <FiMenu className="text-2xl" />
          </button>

          {isMenuOpen && (
            <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-md overflow-hidden z-50">
              <ul className="flex flex-col">
                {navItems.map(item => (
                  <li key={item} className="border-b last:border-b-0">
                    <a
                      href={`#${item.toLowerCase()}`}
                      className="block px-4 py-2 hover:bg-blue-50"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      {item}
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
