// src/components/ProductGrid.jsx
import React from "react";
import { motion } from "framer-motion";

const ProductGrid = ({ products }) => {
  return (
    <div className="w-full px-6">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product) => (
          <div
  key={product.id}
  className="bg-white rounded-xl shadow-md overflow-hidden transform transition duration-300 hover:-translate-y-2 hover:shadow-xl cursor-pointer relative"
>
  <div className="overflow-hidden rounded-t-xl relative">
          <motion.img
            src={product.image}
            alt={product.name}
            className="w-30 h-48 object-contain mx-auto absolute inset-0"
            initial={{ opacity: 1 }}
            whileHover={{ opacity: 0 }}
            transition={{ duration: 0.4 }}
          />
          <motion.div
            className="absolute inset-0 flex items-center justify-center bg-white text-blue-700 text-xl font-bold"
            initial={{ opacity: 0 }}
            whileHover={{ opacity: 1 }}
            transition={{ duration: 0.4 }}
          >
            {product.name}
          </motion.div>
        </div>

        {/* Product Info */}
        <div className="p-4 text-center">
          <p className="text-gray-700 font-bold">{product.price}</p>
        </div>
      </div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
