import React, { useState } from "react";
import { motion } from "framer-motion";

const ProductGrid = ({ products }) => {
  // Array of hover colors
  const hoverColors = ["#D3D3D3","#B6D0E2", "#9FE2BF", "#FEF3C7", "#e5d0ff", "#FAA0A0"]; // blue, green, yellow, red
  const overlayColors = ["#818589","#4682B4", "#10B981", "#FFD700	", "#bf8bff", "#EC5800"]; // Solid versions

  // Track hover state per grid item
  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <div className="w-full px-6">
  {/* Desktop Grid: hidden on small screens */}
  <div className="hidden md:grid md:grid-cols-3 lg:grid-cols-4 gap-8">
    {products.map((product, index) => (
      <div key={product.id} className="group relative rounded-xl shadow-md overflow-hidden">
        {/* Product content */}
      </div>
    ))}
  </div>

  {/* Mobile scroll: only visible on small screens */}
  <div className="md:hidden overflow-x-auto mt-4">
    <div className="flex gap-4 px-2">
      {products.map((product) => (
        <div key={product.id} className="min-w-[200px] rounded-xl shadow-md p-4 flex-shrink-0">
          {/* Product content */}
        </div>
      ))}
    </div>
  </div>
</div>
  );
};

export default ProductGrid;
