import React, { useState, useEffect } from "react";
import { FiMenu } from "react-icons/fi";

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className={`fixed w-full z-50 transition-all duration-300 ${
        isScrolled ? "bg-white/90 shadow-md" : "bg-transparent"
      }`}
    >
      <nav className="container mx-auto px-6 py-4 flex justify-between items-center relative">
        {/* Brand */}
        <h1
          className={`text-2xl font-extrabold text-blue-600 tracking-wide transition-opacity duration-300 ${
            isScrolled ? "opacity-0 pointer-events-none" : "opacity-100"
          }`}
        >
          BrandName
        </h1>

        {/* Right side */}
        <div className="flex items-center relative">
          {isScrolled ? (
            <div className="relative">
              {/* Circle Menu */}
              <button
                className="w-12 h-12 flex items-center justify-center bg-blue-600 text-white rounded-full 
                           hover:bg-blue-700 transition duration-200 shadow-md"
                onClick={() => setIsMenuOpen(!isMenuOpen)}
              >
                <FiMenu className="text-2xl" />
              </button>

              {/* Dropdown */}
              <div
                className={`absolute right-0 mt-2 bg-white shadow-lg rounded-lg py-4 z-50 min-w-[150px]
                           transition-all duration-200 ease-in-out ${
                             isMenuOpen ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-2 pointer-events-none"
                           }`}
              >
                <ul className="flex flex-col gap-3 text-md font-medium px-6">
                  {["Home", "About", "Products", "Features", "Contact"].map((item) => (
                    <li key={item}>
                      <a
                        href={`#${item.toLowerCase()}`}
                        className="hover:text-blue-500 block"
                        onClick={() => setIsMenuOpen(false)}
                      >
                        {item}
                      </a>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          ) : (
            <ul className="flex gap-8 text-md font-medium">
              {["Home", "About", "Products", "Features", "Contact"].map((item) => (
                <li key={item}>
                  <a
                    href={`#${item.toLowerCase()}`}
                    className="relative hover:text-blue-500 after:content-[''] after:absolute after:w-0 after:h-[2px] after:bg-blue-500 after:left-0 after:-bottom-1 after:transition-all after:duration-300 hover:after:w-full"
                  >
                    {item}
                  </a>
                </li>
              ))}
            </ul>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Home;
