import React from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  return (
    <div className="flex flex-col items-start space-y-4">
      {titles.map((title, index) => {
        const isActive = index === value;
        return (
          <motion.div
            key={index}
            onClick={() => onChange?.(index)}
            className={`cursor-pointer px-6 py-3 rounded-lg shadow-md select-none transition-all ${
              isActive ? "bg-white text-blue-600 font-bold" : "bg-gray-200 text-gray-600"
            }`}
            whileHover={{ scale: 1.05 }}
            animate={{ scale: isActive ? 1.05 : 1 }}
            transition={{ duration: 0.2 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
