import React from "react";

const ProductGrid = ({ products }) => {
  return (
    <div className="w-full px-6">
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {products.map((product, index) => (
          <div
  key={product.id}
  className="group relative bg-white rounded-xl shadow-md overflow-hidden transform transition-all duration-300 hover:-translate-y-2 hover:shadow-lg hover:border hover:border-gray-200 cursor-pointer"
>
  {index === 0 ? (
    <div className="flex items-center justify-center h-64 p-6 text-center bg-gray-50">
      <h2 className="text-3xl font-bold text-gray-800">
        Feel free to browse our wares
      </h2>
    </div>
  ) : (
    <div className="relative w-full h-64 overflow-hidden">
      <img
        src={product.image}
        alt={product.name}
        className="w-32 h-48 object-contain mx-auto transition duration-300 group-hover:opacity-75"
      />
      <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition duration-300">
        <span className="text-white text-lg font-semibold">{product.name}</span>
      </div>
    </div>
  )}
  {index !== 0 && (
    <div className="p-4 text-center">
      <h3 className="text-lg font-semibold text-gray-900 mb-2">{product.name}</h3>
      <p className="text-gray-600 font-medium">{product.price}</p>
    </div>
  )}
</div>
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
