import { motion, useScroll, useTransform } from "framer-motion";

const ParallaxTitle = () => {
  const { scrollY } = useScroll();

  // Adjust 0–1000 for distance to match your page
  const y = useTransform(scrollY, [0, 1000], [100, -100]);

  return (
    <section className="h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 to-purple-200 overflow-hidden">
      <motion.h2
        style={{ y }}
        className="text-5xl md:text-6xl font-extrabold text-purple-700 text-center px-6"
      >
        Let's Get in Touch
      </motion.h2>
    </section>
  );
};

export default ParallaxTitle;
