import React, { useState } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [activeIndex, setActiveIndex] = useState(value);

  const handleClick = (index) => {
    setActiveIndex(index);
    onChange?.(index);
  };

  return (
    <div className="flex justify-center items-center flex-wrap gap-6 lg:gap-10">
      {titles.map((title, index) => {
        const isActive = index === activeIndex;
        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            className={`cursor-pointer px-4 py-2 rounded-lg shadow select-none text-center transition-all duration-300 ${
              isActive ? "bg-white text-blue-600 font-bold scale-110" : "bg-gray-200 text-gray-700"
            }`}
            whileHover={{ scale: 1.1 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
