import React from "react";
import { motion, AnimatePresence } from "framer-motion";

const RotatingTitle = ({ titles, activeIndex }) => {
  return (
    <div className="h-16 flex items-center justify-center text-3xl font-bold overflow-hidden">
      <AnimatePresence mode="wait">
        <motion.div
          key={titles[activeIndex]}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.6 }}
        >
          {titles[activeIndex]}
        </motion.div>
      </AnimatePresence>
    </div>
  );
};

export default RotatingTitle;
