import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [activeIndex, setActiveIndex] = useState(value);
  const containerRef = useRef(null);
  const [radius, setRadius] = useState(80); // bigger radius for wider container

  const handleClick = (index) => {
    setActiveIndex(index);
    onChange?.(index);
  };

  // Adjust radius dynamically
  useEffect(() => {
    const updateRadius = () => {
      if (containerRef.current) {
        const size = containerRef.current.offsetWidth;
        setRadius(Math.min(size / 2 - 40, 100)); // limit radius
      }
    };
    updateRadius();
    window.addEventListener("resize", updateRadius);
    return () => window.removeEventListener("resize", updateRadius);
  }, []);

  const total = titles.length;

  return (
    <div
      ref={containerRef}
      className="relative w-full h-64 flex justify-center items-center"
    >
      {titles.map((title, index) => {
        const angleStep = (2 * Math.PI) / total;
        const angle = index * angleStep;
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === activeIndex;

        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            className={`absolute cursor-pointer px-4 py-2 rounded-lg shadow select-none text-center ${
              isActive ? "bg-white text-blue-600 font-bold" : "bg-gray-200 text-gray-700"
            }`}
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: "translate(-50%, -50%)",
              zIndex: isActive ? 20 : 5,
            }}
            animate={{ scale: isActive ? 1.2 : 1 }}
            transition={{ duration: 0.3 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
