import { motion, useScroll, useTransform } from "framer-motion";

const ParallaxTitle = () => {
  // Track scroll relative to the whole page
  const { scrollY } = useScroll();

  // Map scrollY (absolute page scroll) to vertical movement
  const y = useTransform(scrollY, [0, 800], [50, -150]); // adjust 800/-150 for speed & distance

  return (
    <section
      id="contact"
      className="h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 to-purple-200 overflow-hidden"
    >
      <motion.h2
        style={{ y }}
        className="text-5xl md:text-6xl font-extrabold text-purple-700 text-center px-6"
      >
        Let's Get in Touch
      </motion.h2>
    </section>
  );
};

export default ParallaxTitle;
