import React from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  return (
    <div className="flex flex-col items-center md:items-end gap-4">
      {titles.map((title, index) => {
        const isActive = index === value;
        return (
          <motion.div
            key={index}
            onClick={() => onChange(index)}
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 20 }}
            transition={{ duration: 0.3 }}
            className={`cursor-pointer px-5 py-3 rounded-xl text-sm font-medium transition-all duration-300 shadow-md text-center md:text-right ${
              isActive
                ? "bg-white text-blue-600 scale-110 shadow-lg"
                : "bg-gray-200 text-gray-700 hover:bg-white hover:text-blue-600"
            }`}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
