// src/pages/Home.jsx
import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Toys from '../assets/images/toys.png';
import Books from '../assets/images/books.png';
import Clothes from '../assets/images/clothing.png';
import Gadgets from '../assets/images/gadget.png';
import Misc from '../assets/images/misc.png';
import ProductGrid from "../components/ProductGrid";
import AnimatedHeading from "../components/AnimatedHeading";
import RotatingTitles from "../components/RotatingTitle";

const phrases = ["Fast Delivery", "Affordable Prices", "Quality Guaranteed", "24/7 Support"];
const titles = ["Overview", "What do we have?", "Rare stuff"];

const Home = () => {
  const titles = ["Overview", "What do we have?", "Rare stuff"];
  const [selectedIndex, setSelectedIndex] = useState(0);

  const contentMap = [
    "This is the overview of our platform. Here you can learn more about what we do.",
    "We have a variety of amazing products ranging from electronics to rare collectibles.",
    "Looking for something unique? We’ve got rare and exclusive items just for you."
  ];

  return (
    <section
      id="titles"
      className="flex flex-col lg:flex-row items-center justify-center bg-gradient-to-br from-blue-700 to-blue-900 py-10"
      style={{ minHeight: "400px" }}
    >
      {/* Left: Rotating Titles */}
      <div className="w-full lg:w-1/2 flex justify-center">
        <RotatingTitles titles={titles} onChange={(index) => setSelectedIndex(index)} />
      </div>

      {/* Right: Content */}
      <div className="w-full lg:w-1/2 flex justify-center items-center px-8 text-white text-lg">
        <motion.div
          key={selectedIndex}
          initial={{ opacity: 0, x: 30 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -30 }}
          transition={{ duration: 0.4 }}
        >
          {contentMap[selectedIndex]}
        </motion.div>
      </div>
    </section>
  );
};

export default Home;
