import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [radius, setRadius] = useState(80);
  const containerRef = useRef(null);

  useEffect(() => {
    const updateRadius = () => {
      if (containerRef.current) {
        const width = containerRef.current.offsetWidth;
        setRadius(Math.min(width / 2 - 40, 100));
      }
    };
    updateRadius();
    window.addEventListener("resize", updateRadius);
    return () => window.removeEventListener("resize", updateRadius);
  }, []);

  const total = titles.length;

  return (
    <div
      ref={containerRef}
      className="relative w-full h-48 flex justify-center items-center"
    >
      {titles.map((title, index) => {
        const angle = (index / total) * Math.PI - Math.PI / 2; // Half circle
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === value;

        return (
          <motion.div
            key={index}
            onClick={() => onChange(index)}
            whileHover={{ scale: 1.15, boxShadow: "0 5px 15px rgba(0,0,0,0.2)" }}
            animate={{ scale: isActive ? 1.3 : 1 }}
            transition={{ duration: 0.3 }}
            className={`absolute px-4 py-2 rounded-xl text-sm text-center cursor-pointer transition-colors duration-300 ${
              isActive
                ? "bg-white text-blue-600 font-bold"
                : "bg-gray-200 text-gray-700 hover:bg-white hover:text-blue-600"
            }`}
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: "translate(-50%, -50%)",
              zIndex: isActive ? 20 : 5,
              whiteSpace: "nowrap",
            }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
