import { motion, useScroll, useTransform } from "framer-motion";
import { useRef, useState } from "react";

const ParallaxTitle = () => {
  const ref = useRef(null);

  // Track scroll progress of this section
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"], // top enters viewport → bottom leaves
  });

  // Map scroll progress to vertical movement
  const y = useTransform(scrollYProgress, [0, 1], [50, -50]); // adjust 50/-50 for speed

  return (
    <section
      ref={ref}
      id="contact"
      className="h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 to-purple-200 overflow-hidden"
    >
      <motion.h2
        style={{ y }}
        className="text-5xl md:text-6xl font-extrabold text-purple-700 text-center px-6"
      >
        Let's Get in Touch
      </motion.h2>
    </section>
  );
};

export default ParallaxTitle;
