import { motion } from "framer-motion";

export default function ParallaxSection() {
  return (
    <section
      id="contact"
      className="h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 to-purple-200"
    >
      <motion.h1
        initial={{ y: 100, opacity: 0 }}       // start below & invisible
        whileInView={{ y: 0, opacity: 1 }}     // move up & fade in
        viewport={{ once: true, amount: 0.5 }} // trigger when 50% of section visible
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-5xl md:text-6xl font-extrabold text-purple-700 text-center px-6"
      >
        Let's Get in Touch
      </motion.h1>
    </section>
  );
}
