import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";

const RotatingTitles = ({ titles = [], value = 0, onChange }) => {
  const [activeIndex, setActiveIndex] = useState(value);
  const containerRef = useRef(null);
  const [radius, setRadius] = useState(60);

  const handleClick = (index) => {
    setActiveIndex(index);
    onChange?.(index);
  };

  // Limit radius so the circle doesn't push content down
  useEffect(() => {
    const updateRadius = () => {
      if (containerRef.current) {
        const width = containerRef.current.offsetWidth;
        setRadius(Math.min(width / 2 - 10, 70));
      }
    };
    updateRadius();
    window.addEventListener("resize", updateRadius);
    return () => window.removeEventListener("resize", updateRadius);
  }, []);

  const total = titles.length;

  return (
    <div
      ref={containerRef}
      className="relative w-full max-w-[180px] flex justify-center items-center"
      style={{ height: radius * 2 }} // remove extra padding
    >
      {titles.map((title, index) => {
        const angle = (index / total) * 2 * Math.PI - Math.PI / 2;
        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const isActive = index === activeIndex;

        return (
          <motion.div
            key={index}
            onClick={() => handleClick(index)}
            className={`absolute cursor-pointer px-3 py-1 rounded-lg shadow select-none text-center text-sm ${
              isActive ? "bg-white text-blue-600 font-bold" : "bg-gray-200 text-gray-700"
            }`}
            style={{
              left: `calc(50% + ${x}px)`,
              top: `calc(50% + ${y}px)`,
              transform: "translate(-50%, -50%)",
              zIndex: isActive ? 20 : 5,
              whiteSpace: "nowrap",
            }}
            animate={{ scale: isActive ? 1.2 : 1 }}
            transition={{ duration: 0.3 }}
          >
            {title}
          </motion.div>
        );
      })}
    </div>
  );
};

export default RotatingTitles;
